--  DDL for View DASHBOARDCAMEMBERT1
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "BI"."DASHBOARDCAMEMBERT1" ("Email") AS 
  select	 count("FACTURE"."FAMODE") as "Email" 
from	"BI"."FACTURE" "FACTURE" 
where 	 "FACTURE"."FAMODE" ='Email' and MONTHS_BETWEEN(sysdate,FADATE) < 2
;
--------------------------------------------------------
--  DDL for View DASHBOARDCAMEMBERT2
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "BI"."DASHBOARDCAMEMBERT2" ("Edi") AS 
