--------------------------------------------------------
--  DDL for View DASHBOARDFACTUREMODE
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "BI"."DASHBOARDFACTUREMODE" ("MOIS", "FAMODE", "NOMBRE_FACTURES") AS 
  select distinct to_char(FADATE, 'YYYY - MM') as mois,FAMODE, count(FANOFACTURE) as Nombre_Factures 
  from facture,SYS.dual 
  where MONTHS_BETWEEN(sysdate,FADATE) < 6 and MONTHS_BETWEEN(sysdate,FADATE) >= 0  
  group by  to_char(FADATE, 'YYYY - MM'),FAMODE
;
--------------------------------------------------------
--  DDL for View DASHBOARDMONTANTPARMOIS
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "BI"."DASHBOARDMONTANTPARMOIS" ("MOIS", "MONTANT") AS 
  select distinct to_char(FADATE, 'YYYY - MM') as mois,sum(facture.famontantht) as montant 
  from facture,SYS.dual 
  where MONTHS_BETWEEN(sysdate,FADATE) < 6  
  group by  to_char(FADATE, 'YYYY - MM')
;
